+    z-index: 1;
+}
+
+.calcBtn::before {
+    content: ""; 
+    position: absolute;
+    top: 0;
+    left: 0;
+    right: 0;
+    bottom: 0;
+    border-radius: inherit;
+    box-shadow: 0 0 15px rgba(0, 123, 255, 0.8);
+    z-index: -1;
+    animation: glowPulse 5s infinite ease-in-out;
+}
+
+@keyframes glowPulse {
+    0%, 100% {
+        box-shadow: 0 0 5px rgba(0, 123, 255, 0.4),
