+}
+
+.upperBody {
+    display: flex;
+    flex-direction: column;
+    align-items: center;
+    justify-content: center;
+}
+
+.header {
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    margin: 20px;
+    font-size: larger;
+    color: var(--feature-color);
+    font-family: var(--font-family);
+}
+
+.heading {
+    font-size: 50px;
+    font-weight: bolder;
+}
+
+.textBelow {
+    font-size: larger;
+}
+
+.calcBtn {
+    background-color: var(--feature-color);
+    color: black;
+    padding: 5px 12px;
+    border: none;
+    border-radius: 8px;
+    position: relative;
+    z-index: 1;
+}
+
+.calcBtn::before {
+    content: "";
+    position: absolute;
+    top: 0;
+    left: 0;
+    right: 0;
+    bottom: 0;
+    border-radius: inherit;
+    box-shadow: 0 0 15px rgba(0, 123, 255, 0.8);
+    z-index: -1;
+    animation: glowPulse 4s infinite ease-in-out;
+}
+
+@keyframes glowPulse {
+
+    0%,
+    100% {
+        box-shadow: 0 0 10px rgba(0, 123, 255, 0.4),
+            0 0 20px rgba(0, 123, 255, 0.6);
+    }
+
+    50% {
+        box-shadow: 0 0 20px rgba(27, 16, 180, 0.943),
+            0 0 30px rgb(52, 85, 173);
+    }
+}
+
+.card {
+    display: flex;
+    background-color: rgb(10, 24, 25);
+    color: white;
+    border-radius: 20px;
+    padding: 10px;
+    box-sizing: border-box;
+    box-shadow: rgba(17, 12, 46, 0.15) 0px 48px 100px 0px;
+}
+
+.title {
+    display: flex;
+    flex-direction: row;
+    justify-content: space-between;
+    align-items: center;
+}
+
+
+.timeDuration {
+    color: var(--feature-color);
+    box-sizing: border-box;
+    padding: 7px;
+    font-size: smaller;
