@@ -0,0 +1,409 @@
+:root {
+    --bg-color: rgb(6, 15, 17);
+    --font-color: white;
+    --font-family: monospace;
+    --feature-color: rgb(173, 255, 133);
+}
+
+
+body {
+    background-color: var(--bg-color);
+    color: var(--font-color);
+}
+
+
+
+.navbar , .nav-link {
+    color: var(--font-color) !important;
+    font-family: var(--font-family);
+
+}
+
+.navbar-toggler {
+    color: var(--font-color) !important;
+    font-family: var(--font-family);
+}
+
+
+.nav-link {
+    margin-top: 5px;
+    margin-left: 15px;
+}
+
+.custom_btn {
+    background-color: var(--font-color);
+    color: black;
+    justify-content: center;
+    align-items: center;
+    padding-left: 3px;
+    padding-right: 3px;
+    border-radius: 8px;
+    margin-top: 5px;
+    margin-left: 20px;
+    cursor: pointer;
+}
+
+.custom_btn:hover{
+    background-color: var(--feature-color);
+}
+
+.container-fluid {
+    max-width: 250px;
+    margin-right: 80px;
+}
+
+.upperBody {
+    align-items: center;
+    justify-content: center;
+    display: flex;
+    flex-direction: column; 
+}
+
+.header {
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    margin: 20px;
+    font-size: larger;
+    color: var(--feature-color);
+    font-family: var(--font-family);
+}
+
+.heading {
+    font-size: 50px;
+    font-weight: bolder;
+    align-items: center;
+    justify-content: center;
+}
+
+.textBelow {
+    font-size: larger;
+    align-items: center;
+    justify-content: center;
+}
+
+.calcBtn {
+    background-color: var(--feature-color);
+    color: black;
+    padding: 5px 12px;
+    border: none;
+    border-radius: 8px;
+    position: relative;
+    z-index: 1;
+    text-decoration:none ;
+    cursor:pointer;
+}
+
+.calcBtn::before {
+    content: "";
+    position: absolute;
+    top: 0;
+    left: 0;
+    right: 0;
+    bottom: 0;
+    border-radius: inherit;
+    box-shadow: 0 0 15px rgba(0, 123, 255, 0.8);
+    z-index: -1;
+    animation: glowPulse 4s infinite ease-in-out;
+}
+
+@keyframes glowPulse {
+
+    0%,
+    100% {
+        box-shadow: 0 0 10px rgba(0, 123, 255, 0.4),
+            0 0 20px rgba(0, 123, 255, 0.6);
+    }
+
+    50% {
+        box-shadow: 0 0 20px rgba(27, 16, 180, 0.943),
+            0 0 30px rgb(52, 85, 173);
+    }
+}
+
+.card {
+    display: flex;
+    background-color: rgb(10, 24, 25);
+    color: var(--font-color);
+    border-radius: 20px;
+    padding: 10px;
+    box-sizing: border-box;
+    box-shadow: rgba(17, 12, 46, 0.15) 0px 48px 100px 0px;
+}
+
+.title {
+    display: flex;
+    flex-direction: row;
+    justify-content: space-between;
+    align-items: center;
+}
+
+
+.timeDuration {
+    color: var(--feature-color);
+    box-sizing: border-box;
+    padding: 7px;
+    font-size: smaller;
+    font-weight: bolder;
+    border-radius: 12px;
+    box-shadow: var(--feature-color) 0px 4px 5px -2px, var(--feature-color) 0px 3px 7px -3px;
+}
+
+.price {
+    font-size: 50px;
+    font-weight: bolder;
+}
+
+.priceBtn {
+    width: 100%;
+    padding: 10px;
+    border-radius: 8px;
+    box-sizing: border-box;
+    border: none;
+    box-shadow: rgba(84, 237, 112, 0.471) 0px 6px 12px -2px, rgba(63, 196, 54, 0.3) 0px 3px 7px -3px;
+    background-color: var(--feature-color);
+    transition: transform 0.25s  ease-out, box-shadow 0.25s ease-out;
+}
+
+.priceBtn:hover {
+    transform: translateY(-3px);
+    background-color: rgb(196, 244, 171);
+    box-shadow: 0 0 15px rgba(138, 214, 152, 0.8),
+        0 0 25px rgba(149, 223, 144, 0.6);
+}
+
+.allCards {
+    display: flex;
+    margin-top: 50px;
+    margin-left: 50px;
+    margin-right: 20px;
+}
+
+#multipleCardholder {
+    display: flex;
+    align-items: center;
+}
+
+/* .numberCards {
+    display: flex;
+    margin-left: 100px;
+} */
+
+
+.right-grid {
+    display: flex;
+    flex-wrap: wrap;
+    gap: 20px;
+    justify-content: center;
+
+}
+
+.card_1 , .card_4 {
+    display: flex;
+    flex-direction: column;
+    box-sizing: border-box;
+    background-color: rgb(9,22,23);
+    border-radius: 8PX;
+    justify-content: center;
+    align-items: center;
+    flex-grow: 1;
+    flex-shrink: 1;
+    flex-basis: 160px;
