:root {
    --bg-color: rgb(6, 15, 17);
    --font-color:white;
    --font-family: monospace;
    --feature-color: rgb(130, 214, 117);
}


body {
    background-color: var(--bg-color);
    color:var(--font-color);
}


.navbar .nav-link {
    color: var(--font-color) !important;
    font-family: var(--font-family);
    
}

.navbar-toggler {
    color: var(--font-color) !important;
    font-family: var(--font-family);
}


.nav-link {
    margin-top:5px;
    margin-left: 15px;
}
.custom_btn{
    margin-top: 5px;
    margin-left: 20px;
}

.container-fluid {
  max-width: 250px;
  margin-right: 80px;
}

.upperBody{
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}
.header{
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 20px;
    font-size: larger;
    color: var(--feature-color);
    font-family: var(--font-family);
}

.heading{
    font-size:50px;
    font-weight: bolder;
}

.textBelow{
    font-size: larger;
}

.calcBtn{
    background-color: var(--feature-color);
    color: black;
    padding: 5px;
    border: none;
    border-radius: 8px;
}
