commits.content:
#showBtn {
    margin-top: 10px;
    margin-left: 45%;
    margin-right: 35%;
}

.profile-img {
    width: 90px;
    height: 90px;
    object-fit: cover;
    border-radius: 50%;
    margin: auto;
    display: block;
}

.carousel-item {
    text-align: center;
    padding: 20px;
    background-color: rgb(21, 122, 173);
}

.desc {
    width: 100%;
    align-items: center;
    justify-content: center;
    box-sizing: border-box;
    box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
    border-radius: 12px;
    background-color: white;
    font-size: larger;
    padding: 20px;
}
