   display: inline-block;
   transition: background-color 0.6s ease;
 }
 
+ .desc {
+    box-sizing: border-box;
+    box-shadow: rgba(0, 0, 0, 0.24) 0px 3px 8px;
+    padding: 10px;
+    border-radius: 6px;
+    background-color: white;
+    position: relative;
+    max-width: 100%;
+    white-space: nowrap;
+    overflow: hidden;
+    text-overflow: ellipsis;
+    cursor: pointer;
+}
+
+/* Tooltip */
+.desc .tooltip {
+    visibility: hidden;
+    background: black;
+    color: white;
+    padding: 5px 8px;
+    border-radius: 6px;
+    position: absolute;
+    bottom: 100%;
+    left: 50%;
+    transform: translateX(-50%);
+    z-index: 9999;
+    white-space: normal;  /* allow wrapping in tooltip */
+    max-width: inherit;   /* match desc width */
+    box-sizing: border-box;
+}
+
+.desc .tooltip::after {
+    content: "";
+    position: absolute;
+    top: 100%;
+    left: 50%;
+    margin-left: -5px;
+    border-width: 5px;
+    border-style: solid;
+    border-color: black transparent transparent transparent;
+}
+
+.desc:hover .tooltip {
+    visibility: visible;
+}

