+    let valB = b.cells[colIndex].textContent.trim();
+
+    if (!isNaN(valA) && !isNaN(valB)) {
+      valA = +valA;
+      valB = +valB;
+    }
+
+    return sortDirections[colIndex]
+      ? valA > valB ? 1 : -1
+      : valA < valB ? 1 : -1;
