+    footer.innerHTML += `<button onclick="gotoPage(${i})" ${i === currentPage ? "class='activePage'" : ''}>${i}</button>`;
+  }
+  footer.innerHTML += `<button onclick="gotoPage(${currentPage + 1})"${currentPage === totalPages ? 'disabled' : ''}>Next</button>`;
+}
