+    });
+    console.log(tableData);
+
+    const rowId = String(rowData[0]).trim();
+    if (!duplicateId.includes(rowId)) {
+      tableData.push(rowData);
+    }
+  });
+
+  localStorage.setItem('tabledata', JSON.stringify(tableData));
+}
