let questionAnswer = [{
    Question: "Inside which HTML element do we put the JavaScript?",
    Options: ["scripting", "script", "js", "javascript"],
    Answer: "<script>",
    Iscorrect: "false"
},

{
    Question: "Where is the correct place to insert a JavaScript?",
    Options: ["body tag", "head", "both", "none"],
    Answer: "both",
    Iscorrect: "false"
},

{
    Question: "How many days in month?",
    Options: ["30", "45", "15", "7"],
    Answer: "30",
    Iscorrect: "false"
},

{
    Question: "How many days in week?",
    Options: ["30", "45", "15", "7"],
    Answer: "7",
    Iscorrect: "false"
},

{
    Question: "How many months in year?",
    Options: ["30", "12", "15", "7"],
    Answer: "12",
    Iscorrect: "false"
}
]

localStorage.setItem('quiz', JSON.stringify(questionAnswer));
let i=0;

function loop(i){
    document.getElementById("startBtn").addEventListener('click',function(i){
    console.log(i)
    let storedData = JSON.parse(localStorage.getItem('quiz'));
    let startBtn = document.getElementById("container");
    startBtn.style.display = "none";
    let questionCard = document.getElementById("questioncard");
        questionCard.innerHTML += `<div class="card">${storedData[i].Question}</div>
        <div class="form-check">
        <input class="form-check-input" type="radio" name="radioDefault" id="radioDefault1">
        <label class="form-check-label" for="radioDefault1">
         ${storedData[i].Options[0]}
         </label>
        </div>
        <div class="form-check">
        <input class="form-check-input" type="radio" name="radioDefault" id="radioDefault1">
        <label class="form-check-label" for="radioDefault1">
         ${storedData[i].Options[1]}
         </label>
        </div>
        <div class="form-check">
        <input class="form-check-input" type="radio" name="radioDefault" id="radioDefault1">
        <label class="form-check-label" for="radioDefault1">
         ${storedData[i].Options[2]}
         </label>
        </div>
        <div class="form-check">
        <input class="form-check-input" type="radio" name="radioDefault" id="radioDefault1">
        <label class="form-check-label" for="radioDefault1">
         ${storedData[i].Options[3]}
         </label>
        </div>
        <button id="submitBtn" class="btn btn-success" onclick="submitAction(this)">Submit</button>`;

})


function submitAction(){
    let submitBtn=document.getElementById("submitBtn");
    submitBtn.innerText='Next';
        submitBtn.innerText=='Next'? nextAction() :"";
}

function nextAction(){
    console.log(i)
    i++;
    loop(i)
}
}
