function storageToScreen() {
    let Data = JSON.parse(localStorage.getItem('toDo'));
    let toDoList = document.getElementById('multiplecardholder');
    toDoList.innerHTML = "";

    for (let i = 0; i < Data.length; i++) {
        let completedTask = Data[i].isCompleted == "true" ? 'class="complete"' : "";
        toDoList.innerHTML += `<div id="taskCards" ${completedTask}><span >${Data[i].Tasks}</span><span id="editBtn" onclick="editTask(this)">✏️</span><span id="deletebtn" onclick="deleteData(this)">❎</span> <span id="markasDonebtn" onclick="markAsDone(this)">✅</span></div>`

