function validateInputs(){
    let isValid=true;
    let storedData=JSON.parse(localStorage.getItem('toDo'));
    let task = document.getElementById('taskadder').value;
    for(let i=0;i<storedData?.length;i++){
        if(task==storedData[i].Tasks){
            alert("task already exists");
            isValid=false;
        }
    }
    return isValid;
}

document.getElementById('taskAddbtn').onclick = () => {
    if(! validateInputs(true)) return;
    let storedData = JSON.parse(localStorage.getItem('toDo'));
    let task = document.getElementById('taskadder').value;
    let toDoList = document.getElementById('multiplecardholder');
    // toDoList.innerHTML += `<div id="taskCards"> <button id="index">${(storedData?.length) + 1}) </span><span > ${task}</span> <span class="container"><button onclick="deleteData(this)">Delete</button> <button onclick="markAsDone(this)">Done</button></span></div>`
    // toDoList.innerHTML += `<div class="md-3 mb-3"><div class="card"><div class="card-body">  ${task} <span id="container"> <button id="deletebtn" onclick="deleteData(this)">Delete</button><button id="markasDonebtn" onclick="markAsDone(this)">Done</button></span></div></div></div>`
    toDoList.innerHTML += `<div class="md-3 mb-3" > ${task} <span id="container"> <button id="deletebtn" onclick="deleteData(this)">Delete</button><button id="markasDonebtn" onclick="markAsDone(this)">Done</button></span></div></div></div>`

    storedData == null ? storedData = [] : storedData = JSON.parse(localStorage.getItem('toDo'));
    storedData.push({ Tasks: task, isCompleted: "false" });
    // storedData.push({ Id: ((storedData?.length) + 1), Tasks: task });
    localStorage.setItem('toDo', JSON.stringify(storedData));
    clearInputs();
    }


storageToScreen();
