function storageToScreen() {
    let Data = JSON.parse(localStorage.getItem('toDo'));
    let toDoList = document.getElementById('multiplecardholder');
    toDoList.innerHTML = "";

    for (let i = 0; i < Data.length; i++) {
        let completedTask = Data[i].isCompleted == "true" ? 'class="complete"' : "";
        toDoList.innerHTML += `<div id="taskCards" class="col " ${completedTask}>${Data[i].Tasks} <span id="container"> <button id="deletebtn" onclick="deleteData(this)">Delete</button><button id="markasDonebtn" onclick="markAsDone(this)">Done</button></span></div>`

        // toDoList.innerHTML += `<div id="taskCards"><button id="index">${Data[i].Id})</span><span >${Data[i].Tasks}</span><span class="container"><button onclick="deleteData(this)">Delete</button> <button onclick="markAsDone(this)">Done</button></span></div>`
    }
}



function clearInputs() {
    document.getElementById('taskadder').value = "";
}

//Deletebutton function
function deleteData(obj) {
    let storedData = JSON.parse(localStorage.getItem('toDo'));
    obj.closest("div").remove();
    let id = obj.closest("div").innerText.length;
    let task = obj.closest("div").innerText.slice(0, id - 12);
    finalData = storedData.filter(storedData => storedData.Tasks != task.trim());
    localStorage.setItem('toDo', JSON.stringify(finalData));
}


//Done button function
function markAsDone(obj) {
     let storedData = JSON.parse(localStorage.getItem("toDo"));
    obj.closest("div").style.backgroundColor = " rgb(110, 91, 220)";
    obj.closest("div").style.color = "white";
    let id = obj.closest("div").innerText.length;
    console.log(id)
    let completedWork = obj.closest('div').innerText.slice(0, id-12);
    console.log(completedWork)
    for (let i = 0; i < storedData.length; i++) {
        if (storedData[i].Tasks === completedWork) {
            storedData[i].isCompleted = "true";
        }
    }
    localStorage.setItem('toDo', JSON.stringify(storedData));
}



//CT=completed task
// completedTask == null ? completedTask = [] : completedTask = JSON.parse(localStorage.getItem('toDoComplete'));
// let id = obj.closest("div").innerText.length;
// completedTask.push({ CT: obj.closest('div').innerText.slice(0,id-4) });
// localStorage.setItem('toDoComplete', JSON.stringify(completedTask));


//search box
document.getElementById("search").addEventListener('input', () => {
    let toDoList = document.getElementById('multiplecardholder');
    toDoList.innerHTML = "";
    let search = document.getElementById('search').value;
    let storedData = JSON.parse(localStorage.getItem('toDo'));
    let searchedData = storedData.filter(storedData => storedData.Tasks == search || storedData.Tasks.toLowerCase() == search || storedData.Tasks.substring(0, search.length) == search);
    let taskToShow = "";
    searchedData.forEach((obj) => {
        taskToShow += `<div id="taskCards" class="col ">${obj.Tasks}<span id="container"> <button id="deletebtn" onclick="deleteData(this)">Delete</button><button id="markasDonebtn" onclick="markAsDone(this)">Done</button></span></div>`
    });
    toDoList.innerHTML = taskToShow;

})



//dropdown
document.getElementById("dropdown").addEventListener("change", () => {
    let whatToShow = document.getElementById("dropdown").value;
    showTask(whatToShow);
})
function showTask(whatToShow) {
    let storedData = JSON.parse(localStorage.getItem("toDo"));
    let toDoList = document.getElementById('multiplecardholder');
    toDoList.innerHTML = "";
    if (whatToShow == "Pending") {
        let pendingData = storedData.filter(storedData => storedData.isCompleted !== "true");
        pendingData.forEach((obj) => {
            toDoList.innerHTML += `<div id="taskCards" class="col ">${obj.Tasks} <span id="container"> <span id="container">  <button id="deletebtn" onclick="deleteData(this)">Delete</button> <button id="markasDonebtn" onclick="markAsDone(this)">Done</button></span></div>`
        })
    }
    else if (whatToShow == "Completed") {
        let completedData = storedData.filter(storedData => storedData.isCompleted === "true");
        completedData.forEach((obj) => {
            toDoList.innerHTML += `<div class="taskCards" class="col">${obj.Tasks} <span id="container"> <button id="deletebtn" onclick="deleteData(this)">Delete</button><button id="markasDonebtn" onclick="markAsDone(this)">Done</button></span></div>`
        })


    }
    else if (whatToShow == "All") {
        storageToScreen();
    }
}

//clear complete button
document.getElementById("clearcomplete").onclick = () => {
    let storedData = JSON.parse(localStorage.getItem('toDo'));
    let toDoList = document.getElementById('multiplecardholder');
    toDoList.innerHTML = "";
    let finalData = storedData.filter(obj => obj.isCompleted == "false")
    localStorage.setItem('toDo', JSON.stringify(finalData));
    for (let i = 0; i < finalData.length; i++) {
        toDoList.innerHTML += `<div id="taskCards" class="col ">${finalData[i].Tasks}<button id="deletebtn" onclick="deleteData(this)">Delete</button><button id="markasDonebtn" onclick="markAsDone(this)">Done</button></div>`
    }
}
