+function storageToScreen() {
+    let Data = JSON.parse(localStorage.getItem('toDo'));
+    let toDoList = document.getElementById('multiplecardholder');
+    toDoList.innerHTML = "";
+
+    for (let i = 0; i < Data.length; i++) {
+        let completedTask = Data[i].isCompleted == "true" ? 'class="complete"' : "";
+        toDoList.innerHTML += `<div id="taskCards" ${completedTask}>
+                                <span >${Data[i].Tasks}</span>
+                                <button id="deletebtn" onclick="deleteData(this)">Delete</button>
+                                <button id="markasDonebtn" onclick="markAsDone(this)">Done</button>
+                                </div>`
+
+        // toDoList.innerHTML += `<div id="taskCards"><button id="index">${Data[i].Id})</span><span >${Data[i].Tasks}</span><span class="container"><button onclick="deleteData(this)">Delete</button> <button onclick="markAsDone(this)">Done</button></span></div>`
+    }
+}
+
+
+
+function clearInputs() {
+    document.getElementById('taskadder').value = "";
+}
+
+//Deletebutton function
+function deleteData(obj) {
+    let storedData = JSON.parse(localStorage.getItem('toDo'));
+    obj.closest("div").remove();
+    let id = obj.closest("div").innerText.length;
+    let task = obj.closest("div").innerText.slice(0, id - 3);
+    finalData = storedData.filter(storedData => storedData.Tasks != task);
+    localStorage.setItem('toDo', JSON.stringify(finalData));
+}
+
+
+//Donebutton function
+function markAsDone(obj) {
+    let storedData = JSON.parse(localStorage.getItem("toDo"));
+    let id = obj.closest("div").innerText.length;
+    let completedWork = obj.closest('div').innerText.slice(0, id - 4);
+    obj.closest("div").style.color = "green";
+    for (let i = 0; i < storedData.length; i++) {
+        if (storedData[i].Tasks === completedWork) {
+            storedData[i].isCompleted = "true";
+            
+        }
+    }
+    localStorage.setItem('toDo', JSON.stringify(storedData));
+}
+
+
+
+//CT=completed task
