commits.content:
document.getElementById('taskAddBtn').onclick = ()=>{

    let storedData=JSON.parse(localStorage.getItem('toDo'));
    let task=document.getElementById('taskadder').value;
    let toDoList=document.getElementById('multiplecardholder');
     toDoList.innerHTML += `<span id="IdToFetch">${storedData.length}<span><div id="taskCards">${task}<span class="container"><button onclick="deleteData(this)">Delete</button> <button onclick="markAsDone(this)">Done</button></span></div>`
     storedData==null?storedData=[]:storedData=JSON.parse(localStorage.getItem('toDo'));
     storedData.push({Tasks:task});
     localStorage.setItem('toDo',JSON.stringify(storedData));
    clearInputs();   
}

function clearInputs(){
    document.getElementById('taskadder').value="";
}
function deleteData(obj){
    obj.closest("div").remove();
    
}
function markAsDone(obj){
    obj.closest("div").style.color="green";
}
function storageToScreen(){
    let Data=JSON.parse(localStorage.getItem('toDo'));
    let toDoList=document.getElementById('multiplecardholder');
    for(let i=0;i<Data.length;i++){
     toDoList.innerHTML += `<div id="taskCards">${Data[i].Tasks}<span class="container"><button onclick="deleteData(this)">Delete</button> <button onclick="markAsDone(this)">Done</button></span></div>`
    }
}
storageToScreen();
