+.footerEnd {
+    display: inline-block;
+    position: relative;
+    cursor: pointer;
+}
+
+.footerEnd::after {
+    content: "";
+    position: absolute;
+    left: 0;
+    bottom: -2px;
+    width: 0%;
+    height: 1px;
+    background: var(--feature-color);
+    transition: width 0.3s ease;
+}
+
+.footerEnd:hover::after {
+    width: 100%;
+}
+
+li {
+    display: inline-block;
+    position: relative;
+    cursor: pointer;
+}
+
+li::after {
+    content: "";
+    position: absolute;
+    left: 0;
+    bottom: -2px;
+    width: 0%;
+    height: 1px;
+    background: var(--feature-color);
+    /* background: var(--feature-color); */
+    transition: width 0.3s ease;
+}
+
+li:hover::after {
+    width: 80%;
+}
+
+li:hover {
+    color: var(--feature-color);
+}
+
+.footerEnd:hover {
+    color: var(--feature-color);
+}
