+:root {
+    --bg-color: rgb(14, 14, 14);
+    --font-color: white;
+    --feature-color: rgb(255, 81, 0);
+    --blue-gradient: linear-gradient(225deg, #5688f9 0%, #233eff 100%);
+    --orange-gradient: linear-gradient(225deg, #fe7649 0%, #ff2938 100%)
+}
+
+
+body {
+    text-align: center;
+    background-color: var(--bg-color);
+    color: var(--font-color);
+    align-items: center;
+    justify-content: center;
+
+}
+
+
+
+.bgImg {
+    position: absolute;
+    top: 0;
+    left: 0;
+    width: 100%;
+    height: 100vh;
+    background-size: cover;
+    z-index: -1;
+    background: url('images/background.png');
+}
+
+
+
+@media (min-width:200px) {
+    .container-fluid {
+        max-width: 150px;
+    }
+
+    .buttonGroup {
+        position: absolute;
+        right: -20px;
+        margin-top: 20px;
+    }
+
+    .logoImg {
+        position: absolute;
+        left: -80px;
+        margin-top: 20px;
+    }
+
+    .multipleCards {
+        display: flex;
+        flex-direction: column;
+        width: 100%;
+    }
+
+
+}
+
+@media (max-width:663px) {
+    .bgImg,
+    .logo-slider {
+        display: none;
+    }
+}
+
+@media (max-width:587px) {
+    #CollectionOfimages{
+        display: none;
+    }
+    .showImg{
+        display: block;
+    }
+}
+
+@media (max-width:1000px) {
+
+    .multipleCards,
+    .card1,
+    .card2,
+    .card3,
+    .card4 {
+        display: flex;
+        flex-direction: column !important;
+        align-items: center;
+        width: 100% !important;
+    }
+
+
+    .container_content,
+    .textAndImage {
+        display: flex;
+        flex-direction: column !important;
+    }
+
+    .whatWeDo {
+        display: grid;
+        grid-template-columns: auto !important;
+    }
+
+}
+
+
+.navbar {
+    background-color: var(--bg-color) !important;
+}
+
+.nav-link {
+    color: var(--font-color) !important;
+}
+
+
+
+.navbar-toggler {
+    color: var(--font-color) !important;
+}
+
+
+.nav-link {
+    margin-top: 5px;
+    margin-left: 15px;
+}
+
+.navbar-brand {
+    max-width: 100%;
+
+}
+
+.logoImg {
+    width: 20%;
+}
+
+
+
+
+.textUnderNav {
+    text-align: center;
+    justify-content: center;
+    font-size: 3rem;
+    font-weight: bolder;
+}
+
+.intro {
+    text-align: center;
+    justify-content: center;
+    margin-top: 20px;
+    font-size: 1.3rem;
+}
+
+.callButton {
+    background-color: rgb(84, 109, 255);
+    color: var(--font-color);
+    padding: 20px;
+    border-radius: 50%;
+    margin-right: 10px;
+
+}
+
+.callButton:hover {
+    background-color: var(--feature-color);
+}
+
+
+.login {
+    text-decoration: none;
+    padding: 10px;
+    border-radius: 20px;
+    font-size: larger;
+    color: var(--font-color);
+    background: linear-gradient(180deg,
+            rgb(222, 68, 25) 50%,
+            var(--feature-color) 50%);
+    background-size: 100% 200%;
+    background-position: 0 100%;
+    transition: background-position 1.1s cubic-bezier(.16, .84, .442, .98);
+}
+
+.login:hover {
+    cursor: pointer;
+    background-position: 0 0;
+}
+
+
+
+.container-fluid {
+    display: flex;
+    justify-content: center !important;
+}
+
+.bottomBg {
+    position: absolute;
+    bottom: -150px;
+    left: 0;
+    width: 100%;
+    height: 50vh;
+    background: url("images/bottomBg.png") no-repeat bottom center;
+    background-size: cover;
+    z-index: -2;
+}
+
+.bottomPart {
+    display: flex;
+    flex-direction: column;
+    margin-top: 25px;
+    text-align: center;
+}
+
+
+
+.logo-slider {
+    overflow: hidden;
+    padding: 20px;
+    width: 50%;
+    margin-left: 20%;
+    opacity: 0.5;
+}
+
+
+
+.logo-track {
+
+    display: inline-flex;
+    animation: scroll 10s linear infinite;
+    transition: transform 0.8s cubic-bezier(.5, .5, 0, 1);
+}
+
+.logo-track img {
+    height: 40px;
+    margin: 0 40px;
+    vertical-align: middle;
+}
+
+@keyframes scroll {
+    0% {
+        transform: translateX(0);
+    }
+
+    100% {
+        transform: translateX(-50%);
+    }
+}
+
+.notice {
+    text-align: center;
+    color: var(--feature-color);
+}
+
+.warningBox {
+    margin-left: 50px;
+    width: 1000px;
+    max-width: 90%;
+    height: 51px;
