+    border: none;
+}
+
+.Name,
+.role {
+    text-align: left;
+}
+
+#nextBtn {
+    position: absolute;
+    right: -20px;
+    top: 50%;
+    padding: 10px;
+    display: flex;
+    align-items: center;
+    justify-content: center;
+    border-radius: 100px;
+    border: none;
+}
+
+.background {
+    background-color: rgb(24, 24, 24);
+}
+
+.listHeading {
+    text-align: left;
+}
+
+.headingOfList {
+    opacity: 0.7;
+    font-weight: 400;
+    margin-bottom: 50px;
+}
+
+.footerLast {
+    display: flex;
+    flex-direction: row;
+    justify-content: space-between;
+}
+
+.footerEnd {
+    display: inline-block;
+    position: relative;
+    cursor: pointer;
+}
+
+.footerEnd::after {
+    content: "";
+    position: absolute;
+    left: 0;
+    bottom: -2px;
+    width: 0%;
+    height: 1px;
+    background: var(--feature-color);
+    transition: width 0.3s ease;
+}
+
+.footerEnd:hover::after {
+    width: 100%;
+}
+
+li {
+    display: inline-block;
+    position: relative;
+    cursor: pointer;
+}
+
+li::after {
+    content: "";
+    position: absolute;
+    left: 0;
+    bottom: -2px;
+    width: 0%;
+    height: 1px;
+    background: var(--feature-color);
+    /* background: var(--feature-color); */
+    transition: width 0.3s ease;
+}
+
+li:hover::after {
+    width: 80%;
+}
+
+li:hover {
+    color: var(--feature-color);
+}
+
+.footerEnd:hover {
+    color: var(--feature-color);
+}
+
+.lastGradient {
+    /* position: absolute;
+    bottom:-7800px; */
+    width: 100%; 
+    height: 20vh;
+    background: url("images/bottomBg.png") no-repeat bottom center;
+    background-size: cover;
+}
+
+
+
+.reveal {
+  opacity: 0;
+  transition: all 0.8s ease-out;
+  transform: translateY(-40px);
+}
+
+.reveal.active {
+  opacity: 1;
+  transform: translateY(0);
+}
