+            rgba(204, 203, 203, 0.925) 50%,
+            white 50%);
+    background-size: 100% 200%;
+    background-position: 0 100%;
+    transition: background-position 1.0s cubic-bezier(.16, .84, .442, .98);
+
+}
+
+#connectBtn:hover {
+    cursor: pointer;
+    background-position: 0 0;
+}
+
+/* the container with text and halfCircle */
+.custom_container {
+    width: 100%;
+    margin-top: 70px;
+    background-color: rgb(24, 24, 24);
+    position: relative;
+    overflow: hidden;
+    z-index: -3;
+}
+
+/* blue halfCircle */
+.halfCircle {
+    background: var(--blue-gradient);
+    height: 500px;
+    width: 500px;
+    position: absolute;
+    top: -250px;
+    border-radius: 100%;
+    margin-left: 100px;
+    z-index: -2;
+}
+
+/* text of that container which has two half circle */
+.custom_containerText {
+    visibility: visible;
+    animation-delay: 0.2s;
+    text-align: center;
+    align-items: center;
+    justify-content: center;
+}
+
+
+/* account open btn which goes to login page */
+.accountOpenBtn {
+    text-decoration: none;
+    padding: 20px;
+    border-radius: 50px;
+    border: none;
+    font-size: larger;
+    color: white;
+    background: linear-gradient(180deg,
+            #304be6 50%,
+            #233eff 50%);
+    background-size: 100% 200%;
+    background-position: 0 100%;
+    transition: background-position 1.1s cubic-bezier(.16, .84, .442, .98);
+}
+
+.accountOpenBtn:hover {
+    cursor: pointer;
+    background-position: 0 0;
+}
+
+
+
+/* bottom orange halfcircle */
+.bottomOrangeCircle {
+    background: var(--orange-gradient);
+    width: 170px;
+    height: 170px;
+    position: absolute;
+    bottom: -85px;
+    margin-left: 250px;
+    border-radius: 100%;
+}
+
+
+
+/* cards that says the count of people we have */
+.card_1,
+.card_3 {
+    background-color: rgb(24, 24, 24);
+    padding: 20px;
+    max-width: 180px;
+    width: 100%;
+    display: flex;
+    flex-direction: column;
+    flex-grow: 1;
+    flex-shrink: 1;
+    flex-basis: 15px;
+    margin: 20px;
+    border-radius: 8px;
+}
+
+/* cards that says the count of people we have */
+.card_2,
+.card_4 {
+    background-color: rgb(24, 24, 24);
+    padding: 20px;
+    max-width: 230px;
+    width: 100%;
+    display: flex;
+    flex-direction: column;
+    flex-grow: 1;
+    flex-shrink: 1;
+    flex-basis: 25px;
+    border-radius: 8px;
+}
+
+
+/* this is random pictures */
+.upperCollage,
+.bottomCollage {
+    margin: 20px;
+}
+
+/* random pictures and text that says why algodelta like what we provide */
+.custom_container2 {
+    display: flex;
+    flex-direction: row;
+    text-align: left;
+    margin-top: 50px;
+    background-color: rgb(20, 20, 20);
+    position: relative;
+    width: 100%;
+}
+
+/* 12 stratergies of this platform */
+.imagesWithText {
+    display: flex;
+    flex-direction: row;
+    text-align: left;
+}
+
+/* custom container2 content */
+.container_content {
+    display: flex;
+    flex-direction: row;
+    margin: 50px;
+    margin-bottom: 200px;
+}
+
+/* all 12 strategies */
+.whatWeDo {
