+});
+
+document.querySelectorAll(".carousel-container").forEach((container) => {
+    const slides = document.querySelectorAll('.carousel-slide');
+    const arrowLeft = document.querySelector('.arrow-left');
+    const arrowRight = document.querySelector('.arrow-right');
+    let currentSlide = 0;
+
+    // Function to update carousel display
+    // function updateCarousel() {
+    //     slides.forEach((slide, index) => {
+    //         slide.classList.remove('slide-left', 'slide-center', 'slide-right','leftBehind','rightBehind');
+
+    //         if (index === currentSlide) {
+    //             slide.classList.add('slide-center');
+    //         } else if (index === (currentSlide + 1) % slides.length ||
+    //             (currentSlide === slides.length - 1 && index === 0)) {
+    //             slide.classList.add('slide-right');
+    //         } else {
+    //             slide.classList.add('slide-left');
+    //         }
+    //     });
+
+    //     // Update counters
+    //     counters.forEach((counter, index) => {
+    //         counter.textContent = `${index + 1}/${slides.length}`;
+    //     });
+    // }
+    function updateCarousel() {
+        slides.forEach((slide, index) => {
+            slide.classList.remove(
+                'slide-left', 'slide-center', 'slide-right', 'leftBehind', 'rightBehind'
+            );
+
+            let diff = (index - currentSlide + slides.length) % slides.length;
+
+            if (diff === 0) {
+                slide.classList.add('slide-center');      // middle
+            } else if (diff === 1) {
+                slide.classList.add('slide-right');       // 1 right
+            } else if (diff === 2) {
+                slide.classList.add('rightBehind');       // 2 right
+            } else if (diff === slides.length - 1) {
+                slide.classList.add('slide-left');        // 1 left
+            } else if (diff === slides.length - 2) {
+                slide.classList.add('leftBehind');        // 2 left
+            }
+        });
+
+        // counters.forEach((counter, index) => {
+        //     counter.textContent = `${index + 1}/${slides.length}`;
+        // });
+    }
+
+
+    // Next slide function
+    function nextSlide() {
+        currentSlide = (currentSlide + 1) % slides.length;
+        updateCarousel();
+    }
+
+    // Previous slide function
+    function prevSlide() {
+        currentSlide = (currentSlide - 1 + slides.length) % slides.length;
+        updateCarousel();
+    }
+
+    // Event listeners for arrows
+    arrowRight.addEventListener('click', nextSlide);
+    arrowLeft.addEventListener('click', prevSlide);
+
+    // Auto-advance slides every 5 seconds
+    setInterval(nextSlide, 5000);
+
+    // Initialize carousel
+    updateCarousel();
+});
+
+function answerShow(icon) {
+    const cardBody = icon.closest(".card-body");
+    const span = cardBody.querySelector(".answer");
+
+    icon.classList.toggle("open");
+
+    if (span.style.display === "none" || span.style.display === "") {
+        span.style.display = "block";
+    } else {
+        span.style.display = "none";
+    }
+}
+
+const container = document.querySelector('.custom_container');
+const prevBtn = document.querySelector('.custom-prev');
+const nextBtn = document.querySelector('.custom-next');
+
+const cardWidth = 300; // adjust to card+gap size
+
+function scrollSmooth(offset) {
+  const maxScroll = container.scrollWidth - container.offsetWidth;
+  let newScroll = container.scrollLeft + offset;
+
+  // Wrap instantly (no jerk)
+  if (newScroll > maxScroll) {
+    container.scrollLeft = 0; // instant reset
+    newScroll = offset;       // continue scroll from start
+  }
+  if (newScroll < 0) {
+    container.scrollLeft = maxScroll; // instant reset
+    newScroll = maxScroll - offset;   // continue scroll from end
+  }
+
+  container.scrollTo({ left: newScroll, behavior: 'smooth' });
+}
+
+nextBtn.onclick = () => scrollSmooth(cardWidth);
+prevBtn.onclick = () => scrollSmooth(-cardWidth);
+
+
+

