@@ -0,0 +1,70 @@
+import Card from 'react-bootstrap/Card';
+import { useEffect, useState } from 'react';
+
+
+
+function CardOfInfo() {
+    const [data, setData] = useState([]);
+    const cardPerPage = 10;
+    const [page, setPage] = useState(1);
+
+    useEffect(() => {
+        const lcData = JSON.parse(localStorage.getItem("cardData"));
+        if (Array.isArray(lcData) && lcData.length > 0) {
+            setData(lcData);
+        }
+        else {
+            fetch("https://jsonplaceholder.typicode.com/posts")
+                .then(res => res.json())
+                .then(data => {
+                    setData(data);
+                    localStorage.setItem("cardData", JSON.stringify(data))
+                }
+                )
+        }
+    }, []);
+
+    const indexOfLastPost = page * cardPerPage;
+    const indexOfFirstPost = indexOfLastPost - cardPerPage;
+    const currentPosts = data.slice(indexOfFirstPost, indexOfLastPost);
+    console.log(typeof (currentPosts))
+    return (
+            {currentPosts.map((post,i) =>(
+            <Card key={post.id} style={{ width: '18rem' }}>
+                <Card.Body>
+                    <Card.Title>{post.title}</Card.Title>
+                    <hr></hr>
+                    <Card.Text>
+                        Some quick example text to build on the card title and make up the
+                        bulk of the card's content.
+                    </Card.Text>
+                    <Card.Link href="#">Card Link</Card.Link>
+                    <Card.Link href="#">Another Link</Card.Link>
+                </Card.Body>
+            </Card>
+            )
+        )}
+        );
+        {
+            currentPosts.map((post, i) => (
+                <div key={post.id} className='col-md-4 col-sm-12'>
+                    <div className='card mb-4' >
+                        <div className='card-title' title={post.title} onClick={() => { setShowModal(post) }}><h3>{indexOfFirstPost + i + 1} {truncate(post.title, 16)}</h3></div>
+                        <hr></hr>
+                        <div className='card-body' title={post.body} onClick={() => { setShowModal(post) }}>{truncate(post.body, 100)}</div>
+                        <div className='d-flex flex-row'>
+                            <button onClick={() => handleDelete(post.id)} className='btn btn-danger'><i className="bi bi-trash3-fill"></i></button>
+                            <button onClick={() => {
+                                setForm({ title: post.title, body: post.body });
+                                setEditId(post.id);
+                                setShowForm(true);
+                            }} className='btn btn-warning'><i className="bi bi-pencil-fill"></i></button>
+                        </div>
+                    </div>
+                </div>
+            ))
+        }
+    )
+}
+
+export default CardOfInfo;
