+import React, { useState, useEffect } from 'react'
+
+export default function Crud() {
+    const [showForm, setShowForm] = useState(false);
+    const [form, setForm] = useState({ title: "", description: "" });
+    const [editId, setEditId] = useState(null);
+    const [data, setData] = useState([]);
+
+
+    useEffect(() => {
+        fetch("https://jsonplaceholder.typicode.com/posts")
+            .then(res => res.json())
+            .then(data => setData(data))
+    }, []);
+
+    const handleChange = (event) => {
+        setForm({ ...form, [event.target.name]: event.target.value })
+    };
+    const handleSubmit = (e) => {
+        e.preventDefault();
+
+        if (editId) {
+            fetch("https://jsonplaceholder.typicode.com/posts", {
+                method: "PUT",
+                headers: { 'Content-type': 'application/json; charset=UTF-8', },
+                body: JSON.stringify({ ...form, id: editId })
+            })
+                .then((res => res.json()))
+                .then(updated => {
+                    setData(data.map(data => (data.id === editId ? updated : data)))
+                    setForm({ title: "", description: "" });
+                    setEditId(null);
+                })
+        }
+        else {
+            fetch("https://jsonplaceholder.typicode.com/posts", {
+                method: "POST",
+                headers: { 'Content-type': 'application/json; charset=UTF-8', },
+                body: JSON.stringify(form),
+            })
+                .then(res => res.json())
+                .then(newData => {
+                    setData([newData, ...data]);
+                    setForm({ title: "", description: "" });
+                });
+        }
+    };
+    const handleDelete = (id) => {
+        fetch("https://jsonplaceholder.typicode.com/posts", {
+            method: "DELETE"
+        })
+            .then(() => setData(data.filter(data => data.id !== id)));
+    };
+    const handleReset = () => {
+        setForm({ title: "", description: "" })
+    }
+
+    return (
+        <div className='container'>
+            <button onClick={() => { setShowForm(!showForm) }}>{showForm ? "Hide" : "Show"}</button>
+            <form onSubmit={handleSubmit}>
+                <input type='text'
+                    name='title'
+                    placeholder='Enter title'
+                    onChange={handleChange}
+                    value={form.title}
+                    className='form-control mb-2'
+                    required />
+                <input type='text'
+                    title='Description'
+                    placeholder='Enter Description'
+                    onChange={handleChange}
+                    value={form.description}
+                    className='form-control mb-2'
+                    required />
+            </form>
+            <button className='btn btn-success'>Submit</button>
+            <button className='btn btn-warning' type='button' onClick={()=>{setForm({title="",description=""})}}>Reset</button>
+
+            {/* {data.map((post) => {
+                <div className='card' key={post.id}>
+                    <div className='card-title'>{post.title}</div>
+                    <div className='card-body'>{post.body}</div>
+                </div>
+            })} */}
+
+        </div>
+    )
+
+
+
+
+
+
+}

