+import React, { useState, useEffect } from 'react'
+
+export default function Crud() {
+    const [showForm, setShowForm] = useState(false);
+    const [form, setForm] = useState({ title: "", description: "" });
+    const [editId, setEditId] = useState(null);
+    const [data, setData] = useState([]);
+
+
+    useEffect(() => {
+        fetch("https://jsonplaceholder.typicode.com/posts")
+            .then(res => res.json())
+            .then(data => setData(data))
+    }, []);
+
+    const handleChange = (event) => {
+        //  setForm({ ...form, [event.target.title]: event.target.value})
+    };
+    const handleSubmit = (e) => {
+        e.preventDefault();
+
+        if (editId) {
+            fetch("https://jsonplaceholder.typicode.com/posts", {
+                method: "PUT",
+                headers: { 'Content-type': 'application/json; charset=UTF-8', },
+                body: JSON.stringify({ ...form, id: editId })
+            })
+                .then((res => res.json()))
+                .then(updated => {
+                    setData(data.map(data => (data.id === editId ? updated : data)))
+                    setForm({ title: "", description: "" });
+                    setEditId(null);
+                })
+        }
+        else {
+            fetch("https://jsonplaceholder.typicode.com/posts", {
+                method: "POST",
+                headers: { 'Content-type': 'application/json; charset=UTF-8', },
+                body: JSON.stringify(form),
+            })
+                .then(res => res.json())
+                .then(newData => {
+                    setData([newData, ...data]);
+                    setForm({ title: "", description: "" });
+                });
+        }
+    };
+    const handleDelete = async (id) => {
+        try {
+            await fetch(`https://jsonplaceholder.typicode.com/posts/${id}`, {
+                method: "DELETE"
+            });
+
+            // update local state so UI changes
+            setData(data.filter(post => post.id !== id));
+        } catch (err) {
+            console.error("Delete failed:", err);
+        }
+    };
+
+    // const handleDelete = (id) => {
+    //     fetch("https://jsonplaceholder.typicode.com/posts", {
+    //         method: "DELETE"
+    //     })
+    //         .then(() => setData(data.filter(data => data.id !== id)));
+    // };
+
+    return (
+        <div className='container'>
+            <button className="btn btn-primary mb-2 mt-3" onClick={() => setShowForm(!showForm)}>{showForm ? "Hide" : "Show"}</button>
+
+            {showForm &&
+                <form onSubmit={handleSubmit} className='mb-4'>
+                    <input type='text'
+                        name='title'
+                        placeholder='Enter title'
+                        onChange={handleChange}
+                        value={form.title}
+                        className='form-control mb-2'
+                        required />
+                    <textarea
+                        title='Description'
+                        placeholder='Enter Description'
+                        value={form.description}
+                        onChange={(e) => {
+                            setForm({ ...form, description: e.target.value })
+                        }}
+                        className='form-control mb-2'
+                        required />
+
+                    <button className='btn btn-success' onClick={handleSubmit}>Submit</button>
+                    <button className='btn btn-warning' type='button' onClick={() => { setForm({ title: "", description: "" }) }}>Reset</button>
+                </form>
+
+            }
+
+            <div className='row'>
+                {data.map(post => (
+                    <div key={post.id} className='col-md-4 col-sm-12' style={{ gap: "20px" }}>
+                        <div className='card mb-4'>
+                            <div className='card-title'><h3>{post.id}</h3> {post.title}</div>
+                            <div className='card-body'>{post.body}</div>
+                             <button onClick={handleDelete(post.id)}>Delete</button> 
+                            <button onClick={handleChange(post)}>Edit</button>
+                        </div>
+                    </div>
+                ))}
+            </div>
+
+        </div>
+    )
+
+
+
+
+
+
+}

