+import React, { useState, useEffect } from 'react';
+
+export default function Crud() {
+    const [showForm, setShowForm] = useState(false);
+    const [form, setForm] = useState({ title: "", body: "" });
+    const [editId, setEditId] = useState(null);
+    const [data, setData] = useState([]);
+    const cardPerPage = 10;
+    const [page, setPage] = useState(1);
+
+
+    useEffect(() => {
+        fetch("https://jsonplaceholder.typicode.com/posts")
+            .then(res => res.json())
+            .then(data => setData(data),
+                localStorage.setItem("cardData", JSON.stringify(data))
+            )
+    }, [data]);
+
+    const handleChange = (event) => {
+        setForm({ ...form, [event.target.name]: event.target.value })
+    };
+    const handleSubmit = (e) => {
+        e.preventDefault();
+
+        if (editId) {
+            fetch(`https://jsonplaceholder.typicode.com/posts/${editId}`, {
+                method: "PUT",
+                headers: { 'Content-type': 'application/json; charset=UTF-8', },
+                body: JSON.stringify({ ...form, id: editId })
+            })
+                // .then(res => {
+                //     console.log("PUT called:", res.status);
+                //     return res.json();
+                // })
+                .then(res => res.json())
+                .then(updated => {
+                    setData(data.map(data => (data.id === editId ? updated : data)))
+                    setForm({ title: "", body: "" });
+                    setEditId(null);
+                    localStorage.setItem("cardData", JSON.stringify(updated))
+                })
+        }
+        else {
+            fetch("https://jsonplaceholder.typicode.com/posts", {
+                method: "POST",
+                headers: { 'Content-type': 'application/json; charset=UTF-8', },
+                body: JSON.stringify(form),
+            })
+                .then(res => res.json())
+            // .then(res => {
+            //     console.log("POST called:", res.status)
+            //     return res.json();
+            // })
+            const newId = data.length > 0 ? Math.max(...data.map(p => p.id)) + 1 : 1;
+            const newPost = { id: newId, title: form.title, body: form.body };
+            setData([...data, newPost]);
+            const lastPage = Math.ceil((data.length + 1) / cardPerPage);
+            setPage(lastPage);
+            setForm({ title: "", body: "" });
+        }
+    };
+    const indexOfLastPost = page * cardPerPage;
+    const indexOfFirstPost = indexOfLastPost - cardPerPage;
+    const currentPosts = data.slice(indexOfFirstPost, indexOfLastPost);
+    const handleDelete = async (id) => {
+        try {
+            await fetch(`https://jsonplaceholder.typicode.com/posts/${id}`, {
+                method: "DELETE"
+            });
+            const updatedData = data.filter(post => post.id !== id);
+            const lastPage = Math.max(1, Math.ceil(updatedData.length / cardPerPage));
+           if (page > lastPage) setPage(lastPage);
+            localStorage.setItem("cardData",JSON.stringify(updatedData))
+            setData(updatedData);
+
+        } catch (err) {
+            console.error("Delete failed:", err);
+        }
+    };
+  const posts=JSON.parse( localStorage.getItem('cardData'));
+
+    // const handleDelete = (id) => {
+    //     fetch("https://jsonplaceholder.typicode.com/posts", {
+    //         method: "DELETE"
+    //     })
+    //         .then(() => setData(data.filter(data => data.id !== id)));
+    // };
+
+    return (
+        <div className='container'>
+            <button className="btn btn-primary mb-2 mt-3" onClick={() => setShowForm(!showForm)}>{showForm ? "Hide" : "Show"}</button>
+
+            {showForm &&
+                <form onSubmit={handleSubmit} className='mb-4'>
+                    <input type='text'
+                        name='title'
+                        placeholder='Enter title'
+                        onChange={handleChange}
+                        value={form.title}
+                        className='form-control mb-2'
+                        required />
+                    <textarea
+                        title='body'
+                        placeholder='Enter Description'
+                        value={form.body}
+                        onChange={(e) => {
+                            setForm({ ...form, body: e.target.value })
+                        }}
+                        className='form-control mb-2'
+                        required />
+
+                    <button className='btn btn-success' onClick={handleSubmit}>Submit</button>
+                    <button className='btn btn-warning' type='button' onClick={() => { setForm({ title: "", body: "" }) }}>Reset</button>
+                </form>
+
+            }
+
+            <div className='row'>
+                {currentPosts.map((post,i) => (
+                    <div key={post.id} className='col-md-4 col-sm-12' style={{ gap: "20px" }}>
+                        <div className='card mb-4'>
+                            <div className='card-title'><h3>{indexOfFirstPost + i + 1} {post.title}</h3></div>
+                            <div className='card-body'>{post.body}</div>
+                            <button onClick={() => handleDelete(post.id)}>Delete</button>
+                            <button onClick={() => {
+                                setForm({ title: posts.title, body: posts.body });
+                                setEditId(posts.id);
+                                setShowForm(true);
+                            }}>Edit</button>
+                        </div>
+                    </div>
+                ))}
+            </div>
+            <div>
+                <button onClick={() => { setPage(prev => prev - 1) }} disabled={page === 1}>Prev</button>
+                {Array.from({ length: Math.ceil(data.length / cardPerPage) }, (_, idx) => (
+                    <button
+                        key={idx}
+                        onClick={() => setPage(idx + 1)}
+                        style={{
+                            margin: "5px",
+                            background: page === idx + 1 ? "blue" : "white",
+                            color: page === idx + 1 ? "white" : "black",
+                            border: "1px solid black",
+                            borderRadius: "8px",
+                            padding: "10px",
+                        }}>
+                        {idx + 1}
+                    </button>
+                ))}
+                <button onClick={() => { setPage(prev => prev + 1) }} disabled={page === Math.ceil(data.length / cardPerPage)}>Next</button>
+            </div>
+        </div>
+    )
+
+
+
+
+
+
+}

