+                name="body"
+                rows={3}
+                value={form.body}
+                onChange={handleChange}
+              />
+            </Form.Group>
+          </Form>
+        </Modal.Body>
+        <Modal.Footer>
+          <Button variant="secondary" onClick={handleClose}>
+            <i className="bi bi-x-circle me-1"></i> Cancel
+          </Button>
+          <Button variant="primary" onClick={handleSubmit}>
+            <i className="bi bi-check-circle me-1"></i> Save
+          </Button>
+        </Modal.Footer>
+      </Modal>
+    </>
+  );
+}

