require(["vs/editor/editor.main"], function () {
    monaco.editor.create(document.getElementsByClassName("generatedCode")[0], {
        value: `{
    "type": "strategy_order",
    "exit_on_opposite": false,
    "is_tgt": false,
    "is_sl": false,
    "is_trail_set": false,
    "position_size": "{{strategy.position_size}}",
