const searchInput = document.getElementById('search');
const dropdown = document.getElementById('dropdown');

// Simple debounce function
function debounce(func, delay) {
  let timer;
  return function(...args) {
    clearTimeout(timer);
    timer = setTimeout(() => func.apply(this, args), delay);
  };
}

// Fetch API suggestions
async function fetchSuggestions(query) {
  if(!query) {
    dropdown.style.display = 'none';
    dropdown.innerHTML = '';
    return;
  }
     let scriptType=document.getElementById("dropdown1")?.value;
  try {
    const res = await fetch("https://betabv4.algodelta.com/api/v4/users/jsonbridge/searchscript", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Authorization": token
            },
             body: JSON.stringify({
                script: query,
                type: scriptType
            })
        });

    const data = await res.json();
    const suggestions = data.data;
    console.log(data)
    
    dropdown.innerHTML = suggestions.map(item => `<option>${item.script} (${item.exchange})</option>`).join('');
    dropdown.style.display = suggestions.length ? 'block' : 'none';
  } catch(err) {
    console.error(err);
  }
}

// Add click selection
dropdown.addEventListener('click', e => {
  if(e.target.tagName === 'LI') {
    searchInput.value = e.target.textContent;
    dropdown.style.display = 'none';
  }
});

searchInput.addEventListener('input', debounce(e => {
  fetchSuggestions(e.target.value);
}, 300));


