+:root {
+    --font-color: white;
+    --backgroundOf-feature: rgb(25, 28, 36);
+}
+
+body {
+    background: black;
+    color: white;
+}
+
+.syntax_generator {
+    border: none;
+    padding: 15px;
+    color: var(--font-color);
+    background: var(--backgroundOf-feature);
+    font-weight: 450;
+    margin: 10px;
+}
+
+@media (max-width: 1058px) {
+    .customRow {
+        flex-direction: column !important;
+    }
+
+    .leftSide,
+    .rightCtn {
+        width: 100% !important;
+    }
+    .rightCtn {
+        margin-top: 15px;
+    }
+}
+
+/* .active {
+    background: rgb(42, 48, 56);
+    border: 1px solid var(--font-color);
+    border-radius: 12px;
+    box-sizing: border-box;
+    box-shadow: rgba(198, 195, 195, 0.35) 0px 5px 10px;
+} */
+
+.navbar {
+    background: rgb(42, 48, 56) !important;
+    padding: 20px;
+    border-radius: 12px;
+}
+
+a {
+    color: var(--font-color) !important;
+}
+
+.active {
+    padding: 10px;
+    background: rgb(42, 48, 56);
+    border: 1px solid var(--font-color);
+    border-radius: 12px;
+    box-sizing: border-box;
+    box-shadow: rgba(198, 195, 195, 0.35) 0px 5px 10px;
+}
+
+.card {
+    background: var(--backgroundOf-feature);
+    border-radius: 8px;
+    padding: 20px;
+}
+
+
+select {}
+
+/* Hide default arrow for all selects */
+select {
+    -webkit-appearance: none;
+    -moz-appearance: none;
+    appearance: none;
+    cursor: pointer;
+    border-radius: 8px;
+    background-color: rgb(42, 48, 56);
+    padding: 7px;
+    border-radius: 8px;
+    color: var(--font-color);
+    border: none;
+    min-width: 190px;
+}
+
+/* Optional: Add a custom arrow using ::after on a wrapper */
+.select-wrapper {
+    position: relative;
+}
+
+.select-wrapper::after {
+    content: url(images/chevron-down.svg);
+    /* Custom arrow */
+    position: relative;
+    
+    transform: translateY(-50%);
+    pointer-events: none;
+    filter: brightness(0) invert(1);
+}
+
+.text-muted {
+    color: rgb(109 122 135 / 75%) !important;
+}
+
+b {
+    margin-left: 5px;
+    color: var(--font-color);
+}
+
+#search {
+    background-color: rgb(42, 48, 56);
+    padding: 7px;
+    max-width: 160px;
+    border-top-right-radius: 0px;
+    border-bottom-right-radius: 0px;
+    color: var(--font-color);
+    border: none;
+}
+
+
+input {
+    background-color: rgb(42, 48, 56);
+    padding: 7px;
+    border-radius: 8px;
+    color: var(--font-color);
+    border: none;
+}
+
+.arrow {
+    border: none;
+    border-top-right-radius: 8px;
+    border-bottom-right-radius: 8px;
+    background: rgb(42, 48, 56);
+    border-left: 1px solid white;
+    padding: 7px;
+    color: #ccc;
+}
+
+.arrow img {
+    filter: brightness(0) invert(1);
+}
+
+.arrow img:hover {
+    filter: blur(3px);
+}
+
+.dropdownList {
+    list-style: none;
+    padding: 5px;
+    margin: 0;
+    border: 1px solid rgb(32, 112, 186);
+    background: var(--backgroundOf-feature);
+    color: var(--font-color);
+    max-height: 150px;
+    overflow-y: auto;
+    z-index: 9999;
+    position: absolute;
+    margin-top: 40px;
+    width: 200px;
+    display: none;
+}
+
+.dropdownList li {
+    padding: 5px 10px;
+    cursor: pointer;
+
+}
+
+.dropdownList li:hover {
+    background: rgb(32, 112, 186);
+}
+
+.details img {
+    filter: brightness(0) invert(0.3);
+    margin-left: 5px;
+}
+
+.generatedCode {
+    background-color: rgb(42, 48, 56);
+    border-radius: 8px;
+    width: 100%;
+    height: 700px;
+    font-size: larger;
+}
+
+.textAndButtonContainer {
+    display: flex;
+    justify-content: space-between;
+}
+
+.form-check {
+    display: flex;
+    flex-direction: row;
+    flex-wrap: wrap;
+    margin-top: 10px;
+    justify-content: flex-start;
+    color: var(--font-color);
+}
+
+.buyBtn,
+.sellBtn {
+    padding: 10px;
+    padding-left: 30px;
+    padding-right: 30px;
+    background: rgb(42, 48, 56);
+    color: var(--font-color);
+    border: none;
+    font-size: larger;
+    border-radius: 8px;
+}
+
+.leftSide {
+    border: 1px solid #ccc;
+    border-radius: 8px;
+    padding: 15px;
+    
+}
+
+.disable {
+    filter: brightness(0) invert(0.3) !important;
+}
