commits.content:
:root {
    --font-color: white;
    --backgroundOf-feature: rgb(25, 28, 36);
}

body {
    background: black;
    color: white;
}

.syntax_generator {
    border: none;
    padding: 15px;
    color: var(--font-color);
    background: var(--backgroundOf-feature);
    font-weight: 450;
    margin: 10px;
}

/* .active {
    background: rgb(42, 48, 56);
    border: 1px solid var(--font-color);
    border-radius: 12px;
    box-sizing: border-box;
    box-shadow: rgba(198, 195, 195, 0.35) 0px 5px 10px;
} */

.navbar{
    background: rgb(42, 48, 56) !important;
}

a{
    color: var(--font-color) !important;
}

.active{
    background-color: black;
}

.card{
    background: var(--backgroundOf-feature);
    border-radius: 8px;
    margin: 20px;
}

.leftCtn {
    border: 1px solid #ccc;
    border-radius: 8px;
    padding: 10px;
    width: 45%;
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: flex-start;
    align-content: flex-start;
}

option {
    border-radius: 8px;
    border: none;
}

select {
    background-color: rgb(42, 48, 56);
    padding: 7px;
    border-radius: 8px;
    color: var(--font-color);
    border: none;
}

.text-muted {
    color: rgb(109 122 135 / 75%) !important;
}

b{
    margin-left: 5px;
    color: var(--font-color);
}

#search {
    background-color: rgb(42, 48, 56);
    padding: 7px;
    border-top-right-radius: 0px ;
    border-bottom-right-radius: 0px ;
    color: var(--font-color);
    border: none;
}


input{
    background-color: rgb(42, 48, 56);
    padding: 7px;
    border-radius: 8px;
    color: var(--font-color);
    border: none;
}

.arrow {
    border: none;
    border-top-right-radius: 8px;
    border-bottom-right-radius: 8px;
    background: rgb(42, 48, 56);
    border-left: 1px solid white;
    padding: 7px;
    color: #ccc;
}

.arrow img {
    filter: brightness(0) invert(1);
}

.arrow img:hover {
    filter: blur(3px);
}

.dropdownList {
    list-style: none;
    padding: 5px;
    margin: 0;
    border: 1px solid rgb(32, 112, 186);
    background: var(--backgroundOf-feature);
    color: var(--font-color);
    max-height: 150px;
    overflow-y: auto;
    z-index: 9999;
    position: absolute;
    margin-top: 40px;
    width: 200px;
    display: none;
}

.dropdownList li {
    padding: 5px 10px;
    cursor: pointer;

}

.dropdownList li:hover {
    background: rgb(32, 112, 186);
}
.details img {
    filter: brightness(0) invert(0.3);
}

.generatedCode {
    background-color: rgb(42, 48, 56);
    border-radius: 8px;
    width: 100%;
    height: 700px;
    font-size: larger;
}

.textAndButtonContainer {
    display: flex;
    justify-content: space-between;
}

#toggleSwitchGroup {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    margin-top: 10px;
    justify-content: flex-start;
}

.form-check {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    margin-top: 10px;
    justify-content: flex-start;
    color: var(--font-color);
}

.buyBtn,
.sellBtn {
    padding: 15px;
    padding-left: 55px;
    padding-right: 55px;
    background: rgb(42, 48, 56);
    color: var(--font-color);
    border: none;
    font-size: larger;
}

.leftSide{
    border: 1px solid #ccc;
    border-radius: 8px;
    padding: 15px;  
}

select:disabled {
    opacity: 0.5;
}
