+// Safely add/replace a top-level JSON key in the string shown in Monaco
+function upsertJsonStringKey(key, value, { quote = true } = {}) {
+  if (!editor) return;
+  let code = editor.getValue();
+  const escapedKey = key.replace(/[-/\\^$*+?.()|[\]{}]/g, "\\$&");
+  const pattern = new RegExp(`("` + escapedKey + `"\s*:\s*)([^,}\\n]+)`, "m");
+  const finalVal = quote ? `"${value}"` : `${value}`;
+
+  if (pattern.test(code)) {
+    code = code.replace(pattern, `$1${finalVal}`);
+  } else {
+    // append before the last }
+    code = code.trim();
+    if (code.endsWith("}")) code = code.slice(0, -1);
+    // remove trailing comma if present
+    code = code.replace(/,\s*$/, "");
+    code += `,\n  "${key}": ${finalVal}\n}`;
+  }
+  editor.setValue(code);
 }
 
