// ------------------ Monaco Editor ------------------
let editor;

require.config({ paths: { vs: "https://unpkg.com/monaco-editor@0.52.0/min/vs" } });

require(["vs/editor/editor.main"], function () {
  const container = document.getElementsByClassName("generatedCode")[0];

  function getCodeSnippet(activeType) {
    if (activeType === "strategy_order") {
      return `{
  "type": "strategy_order",
  "exit_on_opposite": false,
  "is_tgt": false,
  "is_sl": false,
  "is_trail_set": false,
  "position_size": "{{strategy.position_size}}",
  "transaction_type": "{{strategy.order.action}}"
}`;
    } else {
      return `{
  "type": "${activeType}"
}`;
    }
  }

  window.getCodeSnippet = getCodeSnippet;

  editor = monaco.editor.create(container, {
    value: getCodeSnippet("strategy_order"),
    language: "json",
    theme: "vs-dark",
    automaticLayout: true,
    fontSize: 18,
    minimap: { enabled: false },
    wordWrap: "on",
    scrollbar: { vertical: "hidden", horizontal: "hidden", handleMouseWheel: false }
  });
});

// ------------------ Navbar ------------------
let activeNav = "strategy_order"; // default
const navLinks = document.querySelectorAll(".nav-link");

navLinks.forEach(link => {
  link.addEventListener("click", e => {
    e.preventDefault();
    navLinks.forEach(l => l.classList.remove("active"));
    link.classList.add("active");
    activeNav = link.getAttribute("data-type");
    localStorage.setItem("activenav", JSON.stringify(activeNav));
    editor.setValue(getCodeSnippet(activeNav));
    resetAllFields();
  });
});

// ------------------ Reset & Copy ------------------
document.getElementById("deleteBtn").addEventListener("click", function () {
  const activeNavItem = JSON.parse(localStorage.getItem("activenav"));
  editor.setValue(getCodeSnippet(activeNavItem));
  resetAllFields();
});

document.getElementById("copyBtn").addEventListener("click", function () {
  if (!editor) return;
  navigator.clipboard.writeText(editor.getValue())
    .then(() => alert("Code copied to clipboard!"))
    .catch(err => console.error(err));
});

// ------------------ Field Dependencies ------------------
function resetAllFields() {
  const containers = [
    "#optionSelection", "#scriptsearchbox", "#premiumval", "#maxvariationCtn",
    "#AtmGap", "#expiryGap", "#expiryType", "#productType", "#quantityBox",
    "#limitPrice", "#triggetPrice", "#BtnGroup", "#toggleSwitchGroup"
  ];
  containers.forEach(c => {
    const cont = document.querySelector(c);
    if (cont) {
      cont.style.display = "none";
      const input = cont.querySelector("input, select");
      if (input) {
        if (input.tagName === "SELECT") input.selectedIndex = 0;
        else input.value = "";
      }
    }
  });
}

// ------------------ Script Type ------------------
const scriptType = document.getElementById("dropdown1");
const optionSelection = document.getElementById("optionSelection");
const searchBox = document.getElementById("scriptsearchbox");

scriptType.addEventListener("change", function () {
  resetAllFields();
  if (activeNav === "strategy_order" && (scriptType.value === "CE" || scriptType.value === "PE")) {
    optionSelection.style.display = "block";
  } else {
    searchBox.style.display = "block";
  }
});

// ------------------ Option Selection ------------------
const atmDropdown = document.getElementById("atmdropdown");
atmDropdown.addEventListener("change", function () {
  resetAllFields();
  document.getElementById("AtmGap").style.display = "block";
});

// ------------------ Script Search ------------------
const searchInput = document.getElementById("search");
const dropdown = document.getElementById('dropdown');
let token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyX2lkIjoyLCJmcmNoX2lkIjoxLCJlbWFpbCI6ImRldjMuYWxnb2RlbHRhQGdtYWlsLmNvbSIsIm1vYmlsZV9ubyI6IjkzMTMxODM4NDciLCJjdXN0X2lkIjoiNzM1NTQ3IiwiZW52IjoibWFpbiIsInVzZXJfdHlwZSI6InVzZXIiLCJpYXQiOjE3NTYxODc4MTIsImV4cCI6MTc1NjI3NDIxMn0.gXoY2DWtFgVNYrud7j88O-KLCwlpkUX6h30ANgYu6TU"; // truncated

document.addEventListener('click', e => {
  if (!searchInput.contains(e.target) && !dropdown.contains(e.target)) dropdown.style.display = 'none';
});

function debounce(func, delay) {
  let timer;
  return function (...args) {
    clearTimeout(timer);
    timer = setTimeout(() => func.apply(this, args), delay);
  };
}

// ------------------ Fetch Suggestions ------------------
async function fetchSuggestions(query) {
  if (!query) {
    dropdown.style.display = 'none';
    dropdown.innerHTML = '';
    return;
  }

  let apiUrl;
  let bodyData = { script: query };

  if (activeNav === "strategy_order") {
    apiUrl = "https://betabv4.algodelta.com/api/v4/users/jsonbridge/searchscript";
    bodyData.type = scriptType.value;
    if (scriptType.value === "CE" || scriptType.value === "PE") {
      bodyData.option_type = atmDropdown.value;
    }
  } else {
    apiUrl = "https://betabv4.algodelta.com/api/v4/users/jsonbridge/searchscriptv2"; // Different API
    bodyData.type = scriptType.value;
  }

  try {
    const res = await fetch(apiUrl, {
      method: "POST",
      headers: { "Content-Type": "application/json", "Authorization": token },
      body: JSON.stringify(bodyData)
    });
    const data = await res.json();
    const suggestions = data.data || [];
    dropdown.innerHTML = suggestions.map(item => `<li>${item.script} (${item.exchange})</li>`).join('');
    dropdown.style.display = suggestions.length ? 'block' : 'none';
  } catch (err) { console.error(err); }
}

searchInput.addEventListener('input', debounce(e => fetchSuggestions(e.target.value), 300));

dropdown.addEventListener('click', e => {
  if (e.target.tagName === 'LI') {
    let selectedText = e.target.textContent.split("(")[0].trim();
    searchInput.value = selectedText;
    dropdown.style.display = 'none';

    let currentValue = editor.getValue().trim();
    if (/"script"\s*:\s*".*?"/.test(currentValue)) {
      currentValue = currentValue.replace(/("script"\s*:\s*").*?(")[\s\S]*$/, `$1${selectedText}$2\n}`);
    } else {
      if (currentValue.endsWith("}")) currentValue = currentValue.slice(0, -1).trim();
      currentValue = currentValue.replace(/,\s*$/, "");
      currentValue += `,\n\t"script":"${selectedText}"\n}`;
    }
    editor.setValue(currentValue);
  }
});
