+                       <span> <Button variant="secondary" onClick={() => navigate("/")} style={{marginRight:"15px"}}>
+                            <i className="bi bi-caret-left-fill"></i>
+                        </Button>
+                        <span ><strong>Name:</strong> {user.name} </span></span>
