+import { useEffect, useState } from "react";
+import { useNavigate, useParams } from "react-router-dom";
+import Card from "react-bootstrap/Card";
+import Button from "react-bootstrap/Button";
+import Pagination from "./Pagination";
+import Form from "react-bootstrap/Form";
+
+export default function UserDetails() {
+    const { id } = useParams();
+    const navigate = useNavigate();
+    const [user, setUser] = useState(null);
+    const [posts, setPosts] = useState([]);
+    const [page, setPage] = useState(1);
+    const [cardPerPage, setCardPerPage] = useState(3)
+
+    useEffect(() => {
+        fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
+            .then((res) => res.json())
+            .then((data) => setUser(data));
+
+        fetch(`https://jsonplaceholder.typicode.com/posts?userId=${id}`)
+            .then((res) => res.json())
+            .then((data) => setPosts(data));
+    }, [id]);
+
+
+    if (!user) return <p>Loading...</p>;
+
+    const startIndex = (page - 1) * cardPerPage;
+    const endIndex = startIndex + cardPerPage;
+    const currentData = posts.slice(startIndex, endIndex)
+
+    return (
+        <div className="container">
+            {/* <Card className="p-3 m-3 shadow " >
+                <Card.Text>
+                    <div className="d-flex justify-content-between titleCard" >
+                        <span> <Button variant="secondary" onClick={() => navigate("/")} style={{ marginRight: "15px" }}>
+                            <i className="bi bi-caret-left-fill"></i>
+                        </Button>
+                            <span ><strong>Name:</strong> {user.name} </span></span>
+                        <span className="mt-1 ml-20"><strong>Email:</strong> {user.email} </span>
+                    </div>
+                </Card.Text>
+            </Card> */}
+            <Card className="p-3 m-3 modern-user-card">
+                <Card.Body className="d-flex align-items-center">
+                    <Button
+                        variant="outline-secondary"
+                        onClick={() => navigate("/")}
+                        className="me-3 back-button"
+                    >
+                        <i className="bi bi-caret-left-fill"></i>
+                    </Button>
+                    <div className="user-info-container">
+                        <div className="user-name">{user.name}</div>
+                        <div className="user-details">
+                            <div className="user-detail">
+                                <i className="far fa-envelope"></i>
+                                {user.email}
+                            </div>
+                        </div>
+                    </div>
+                </Card.Body>
+            </Card>
+            <div className="d-flex flex-row flex-wrap justify-content-center">
+                {currentData.map((post) => (
+                    <Card
+                        key={post.id}
+                        className="p-3 m-2 shadow"
+                        style={{ width: "23rem" }}
+                    >
+                        <Card.Title><span style={{ color: "#4f46e5", fontSize: "25px" }}>Title:</span>{post.title}</Card.Title>
+                        <Card.Text><h5 style={{ color: "#4f46e5" }}>Description:</h5>{post.body}</Card.Text>
+                    </Card>
+                ))}
+            </div>
+            <div className=" paginationCtn">
+                <Form.Select
+                    style={{
+                        maxWidth: "100px",
+                        zIndex: "2"
+                    }}
+                    value={cardPerPage}
+                    onChange={(e) => setCardPerPage(Number(e.target.value))}
+                >
+                    <option value={3}>3</option>
+                    <option value={5}>5</option>
+                    <option value={10}>All</option>
+                </Form.Select>
+                {/* <input
+            type="text"
+            onInput={(e)=>setCardPerPage(Number(e.target.value))}
+            placeholder="Write how much details you want see..."
+            /> */}
+
+                <div className=" pagesToShow" >
+                    <Pagination
+                        total={posts.length}
+                        cardPerPage={cardPerPage}
+                        page={page}
+                        setPage={setPage}
+                    />
+                </div>
+            </div>
+
+        </div>
+
+    );
+}
+

