import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Dropdown from "react-bootstrap/Dropdown";
import Button from "react-bootstrap/Button";
import CardView from "./CardView";
import TableView from "./TableView";

export default function CardList() {
    const [data, setData] = useState([]);
    const [search, setSearch] = useState('');
    const [sortKey, setSortKey] = useState(null);
    const [sortDirection, setSortDirection] = useState('asc');
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [view, setView] = useState("card"); // card or table
    const navigate = useNavigate();

    useEffect(() => {
        fetch("https://jsonplaceholder.typicode.com/users")
            .then(res => res.json())
            .then(data => setData(data));
    }, []);

    const toggleSort = (key) => {
        if (sortKey === key) setSortDirection(sortDirection === "asc" ? "desc" : "asc");
        else {
            setSortKey(key);
            setSortDirection("asc");
        }
    };

    const filteredData = data.filter(item => item.name.toLowerCase().includes(search.toLowerCase()));

    const sortedData = [...filteredData].sort((a, b) => {
        if (!sortKey) return 0;
        const aValue = a[sortKey], bValue = b[sortKey];
        if (aValue < bValue) return sortDirection === "asc" ? -1 : 1;
        if (aValue > bValue) return sortDirection === "asc" ? 1 : -1;
        return 0;
    });

    return (
        <div className="container">
            <div className="d-flex justify-content-between m-3">
                <div>
                    <Button variant="primary" className="me-2" onClick={() => setView("card")}>
                        <i className="bi bi-card-text"></i>
                    </Button>
                    <Button variant="primary" onClick={() => setView("table")}>
                        <i className="bi bi-table"></i>
                    </Button>
                </div>

                <span className="d-flex align-items-center">
                    <label className="me-2">Search:</label>
                    <input type="text" value={search} onChange={e => setSearch(e.target.value)} />
                </span>
            </div>

            {view === "card" ? (
                <CardView data={sortedData} navigate={navigate} />
            ) : (
                <TableView
                    data={sortedData}
                    sortKey={sortKey}
                    sortDirection={sortDirection}
                    toggleSort={toggleSort}
                    navigate={navigate}
                />
            )}
        </div>
    );
}
