import { Table, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

export default function TableView({ data, sortKey, sortDirection, toggleSort, navigate }) {
    const getArrow = (key) => (sortKey === key ? (sortDirection === "asc" ? "⬇️" : "⬆️") : "");
    
    return (
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>ID</th>
                    <th onClick={() => toggleSort("name")} style={{ cursor: "pointer" }}>
                        Name {getArrow("name")}
                    </th>
                    <th onClick={() => toggleSort("email")} style={{ cursor: "pointer" }}>
                        Email {getArrow("email")}
                    </th>
                    <th>Company</th>
                    <th>Action</th>
                </tr>
            </thead>
            <tbody>
                {data.map(user => (
                    <tr key={user.id}>
                        <td>{user.id}</td>
                        <td>{user.name}</td>
                        <td>{user.email}</td>
                        <td>{user.company.name}</td>
                        <td>
                            <Button onClick={() => navigate(`/users/${user.id}`)}>View</Button>
                        </td>
                    </tr>
                ))}
            </tbody>
        </Table>
    );
}
