+import { useState, useEffect } from "react";
+import { useNavigate } from "react-router-dom";
+import Card from "react-bootstrap/Card";
+import Button from "react-bootstrap/Button";
+import Dropdown from "react-bootstrap/Dropdown";
+import { Table } from "react-bootstrap/Table";
+
+export default function CardList() {
+    const [data, setData] = useState([]);
+    const [search, setSearch] = useState('');
+    const navigate = useNavigate();
+    const [sortKey, setSortKey] = useState(null);
+    const [sortDirection, setSortDirection] = useState('asc');
+    const [dropdownOpen, setDropdownOpen] = useState(false);
+
+    useEffect(() => {
+        fetch("https://jsonplaceholder.typicode.com/users")
+            .then(res => res.json())
+            .then(data => setData(data))
+    }, [])
+
+    const toggleSort = (key) => {
+        if (sortKey === key) {
+            setSortDirection(sortDirection === "asc" ? "desc" : "asc");
+        } else {
+            setSortKey(key);
+            setSortDirection("asc");
+        }
+    };
+
+    const filteredData = data.filter(item =>
+        item.name.toLowerCase().includes(search.toLowerCase())
+    );
+
+    const sortedData = [...filteredData].sort((a, b) => {
+        const aValue = a[sortKey];
+        const bValue = b[sortKey];
+        if (aValue < bValue) return sortDirection === "asc" ? -1 : 1;
+        if (aValue > bValue) return sortDirection === "asc" ? 1 : -1;
+        return 0;
+    });
+
+    const getArrow = () => (sortDirection === "asc" ? <img src="C:\Users\admin\Downloads\sort-descending.png"  </img> :  <img src="C:\Users\admin\Downloads\sort.png"> </img>);
+
+    return (
+        <>
+            Search:
+            <input type="text"
+                value={search}
+                onChange={(e) => setSearch(e.target.value)} />
+
+            <Dropdown show={dropdownOpen} onToggle={() => setDropdownOpen(!dropdownOpen)}>
+                <Dropdown.Toggle onClick={() => setDropdownOpen(!dropdownOpen)} variant="info">
+                    Sort Options
+                </Dropdown.Toggle>
+                <Dropdown.Menu style={{ minWidth: "200px" }}>
+                    {["name", "email"].map((key) => (
+                        <Dropdown.Item
+                            as="div"   // prevent automatic close
+                            key={key}
+                            className="d-flex justify-content-between align-items-center"
+                        >
+                            <span>{key.charAt(0).toUpperCase() + key.slice(1)}</span>
+                            <Button
+                                size="sm"
+                                variant="outline-primary"
+                                onClick={(e) => {
+                                    e.stopPropagation();   // prevent closing dropdown
+                                    toggleSort(key);
+                                }}
+                            >
+                                {getArrow(key)}
+                            </Button>
+                        </Dropdown.Item>
+                    ))}
+                </Dropdown.Menu>
+            </Dropdown >
+
+            {/* <select>
+                <option hidden selected>Select Sorting Option</option>
+                <option onClick={() => handleSort('name')}>Name</option>
+                <option onClick={() => handleSort('email')}>Email</option>
+            </select> */}
+
+            < div className="d-flex flex-row flex-wrap justify-content-center" >
+                {
+                    sortedData.map((user) => (
+                        <Card key={user.id} style={{
+                            width: "18rem",
+                            margin: "15px",
+                            padding: "10px"
+                        }}>
+                            <Card.Body>
+                                <Card.Title>{user.id}</Card.Title>
+                                <Card.Text>
+                                    <b>Name:</b>{user.name}<br />
+                                    <b>Email:</b>{user.email}<br />
+                                    <b>Company:</b>{user.company.name}
+                                </Card.Text>
+                            </Card.Body>
+                            <Button className="btn btn-primary" onClick={() => navigate(`/users/${user.id}`)}>View Details</Button>
+                        </Card>
+                    ))
+                }
+            </div >
+        </>
+    )
